using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace Program{    class Factory    {        public static void CreateGraph(string type)        {            switch (type)            {                case "Triangle":                    Triangle triangle = new Triangle();                    triangle.GetArea();                    break;                case "Round":                    Round round = new Round();                    round.GetArea();                    break;                case "Square":                    Square square = new Square();                    square.GetArea();                    break;                case "Rect":                    Rect rect = new Rect();                    rect.GetArea();                    break;            }        }        static void Main(string[] args)        {            try            {                Console.WriteLine("Triangle     Round       Square      Rect");                string type = Console.ReadLine();                CreateGraph(type);            }            catch (Exception ex)            {                Console.WriteLine("error" + ex.Message);            }        }    }}abstract class Graph{    public abstract void GetArea();}class Triangle : Graph{    public double Width = 2.0;    public double Height = 3.0;    public override void GetArea()    {        double Area = Width * Height / 2;        Console.WriteLine("The area of the Triangle is " + Area);    }}class Round : Graph{    public double Radius = 3.0;    public override void GetArea()    {        double Area = Radius * Radius * 3.1415;        Console.WriteLine("The area of the Round is " + Area);    }}class Square : Graph{    public double Length = 3.0;    public override void GetArea()    {        double Area = Length * Length;        Console.WriteLine("The area of the Square is " + Area);    }}class Rect : Graph{    public double Width = 2.0;    public double Height = 3.0;    public override void GetArea()    {        double Area = Width * Height;        Console.WriteLine("The area of the Rect is " + Area);    }}